              }else {
                  echo "<p>Mail dont sent, please try again</p>";
                  echo "<p>Or call us on our phone <a href='tel:0120065899' style='color: green;'>012 006 5899</a>.</p>";
              }
        
        }
    }else{

        show_advices($advices);

    }

   
    
}else{
    echo "<h2>Bad request<h2>";
}


function check_required_fields($required_fields){

    foreach($required_fields as $field) {

        if( !isset($_POST[$field]) ){
            return false;
        }
    }
   return true;
}

function test_input($data) {
    $data = trim($data);
    $data = stripslashes($data);
    $data = htmlspecialchars($data);
    return $data;
}

function clean_fields($fields){
    $cleaned_fields = [];

    foreach($fields as $field){
         $cleaned_fields[$field] = test_input($_POST[$field]);
    }

    return $cleaned_fields;
}

//Show advices if some one of the required field is empty or not correct
function get_advices($cleaned_fields, $required_fields){
    $advices = [];
    foreach($required_fields as $field){
          if($field == "email" && !filter_var($cleaned_fields[$field], FILTER_VALIDATE_EMAIL)){
              array_push($advices, "【" . $field . "】は必須項目です" );
              continue;
          }

          if(empty($cleaned_fields[$field])){
              array_push($advices, "【" . $field . "】は必須項目です" );
          }
    }

    return $advices;
}

function show_advices($advices) {
    $html = "<p class='title' style='color: red;'>入力にエラーがあります。下記をご確認の上「戻る」ボタンにて修正をお願い致します。</p>";
    $html .= "<div style='color: red;'>";
    foreach ($advices as $value) {
        $html .= "<p class='advice-error'>" . $value . "</p>";
    }
    $html .= "<button class='btn-back-history' onClick='history.back()'>戻る</button>";
    $html .= "</div>";
    echo $html;
}


