function body_mail($cleaned_fields) {
    $html = "<table>";
    foreach ($cleaned_fields as $key => $value) {
       $html .= "<tr>";
       $html .= "<p>■ $key ：</p>";
       $html .= "<p>$value</p>";
       $html .= "</tr>";
    }
    $html .= "</table>";
    return $html;
}


